    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
       
        
        String tipo,hora,pago,nombre,telefono,correo,fecha;
        int precio,respu;
        
         
        tipo=tip.getSelectedItem().toString();
        hora=hor.getText(); 
        pago=fPago.getSelectedItem().toString(); 
        nombre=nom.getText();
        telefono=tel.getText();
        correo=cor.getText();
        fecha=fech.getText();
        
        precio=Integer.parseInt(pre.getText());
        
         respu = boletos(tipo, hora, pago,nombre,telefono,correo,fecha,precio);
        String res = String.valueOf(respu);
         
        txtrespu.setText(res);
    }//GEN-LAST:event_jButton2ActionPerformed

