    private static int boletos(java.lang.String tipo, java.lang.String hora, java.lang.String fPago, java.lang.String nombre, java.lang.String telefono, java.lang.String correo, java.lang.String fecha, int precio) {
        WS.CompraBoleto_Service service = new WS.CompraBoleto_Service();
        WS.CompraBoleto port = service.getCompraBoletoPort();
        return port.boletos(tipo, hora, fPago, nombre, telefono, correo, fecha, precio);
    }
